Content.Implementation:
FOR ii:=1 TO 60 BY 1 DO
	IF ii<48 THEN
		aCB[ii].Flow^.instMag := INT_TO_REAL(UDPBansheeRead.i16_Raw[UDPBansheeRead.REAL_ID + ii - 1])/kWMaxBANSHEE;
		aCB[ii].Flow^.q.validity := UDPBansheeRead.bWdgDataValid;
		aCB[ii].Status^.stVal := INT_TO_BOOL(UDPBansheeRead.i16_Raw[UDPBansheeRead.STATUS_ID + ii - 1]);
		aCB[ii].Status^.q.validity := UDPBansheeRead.bWdgDataValid;
		aCB[ii].Vpu^.instMag := INT_TO_REAL(UDPBansheeRead.i16_Raw[UDPBansheeRead.VPU_ID + ii - 1])/100;
		aCB[ii].Vpu^.q.validity := UDPBansheeRead.bWdgDataValid;
		aCB[ii].VLgood^.stVal := aCB[ii].Vpu^.instMag > 0.6 AND aCB[ii].Vpu^.instMag < 1.15;
		aCB[ii].VLgood^.q.validity := UDPBansheeRead.bWdgDataValid;
	ELSE
		aCB[ii].Flow^.instMag := INT_TO_REAL(UDPPHILRead.i16_Raw[UDPPHILRead.REAL_ID + ii - 48])/kWMaxPHIL;
		aCB[ii].Flow^.q.validity := UDPPHILRead.bWdgDataValid;
		aCB[ii].Status^.stVal := INT_TO_BOOL(UDPPHILRead.i16_Raw[UDPPHILRead.STATUS_ID + ii - 48]);
		aCB[ii].Status^.q.validity := UDPPHILRead.bWdgDataValid;
		aCB[ii].Vpu^.instMag := INT_TO_REAL(UDPPHILRead.i16_Raw[UDPPHILRead.VPU_ID + ii - 48])/100;
		aCB[ii].Vpu^.q.validity := UDPPHILRead.bWdgDataValid;
		aCB[ii].VLgood^.stVal := aCB[ii].Vpu^.instMag > 0.6  AND aCB[ii].Vpu^.instMag < 1.15;
		aCB[ii].VLgood^.q.validity := UDPPHILRead.bWdgDataValid;
	END_IF

	IF aCB[ii].IOCCmd <> 0 THEN
		IF aCB[ii].IOCCmd^.operPulse.ctlVal = TRUE THEN
			IF aCB[ii].Status^.stVal = TRUE THEN
				CB_cmd := 2;
			ELSE
				CB_cmd := 0;
			END_IF
		ELSIF aCB[ii].IOCCmd^.operSet.ctlVal = TRUE THEN
			CB_cmd := 1;
		ELSIF aCB[ii].IOCCmd^.operClear.ctlVal = TRUE THEN
			CB_rst := 1;
		ELSE
			CB_cmd := 5;
			CB_rst := 0;
		END_IF
		aCB[ii].MBCmd^.oper.q.validity := validity_t.good;
		aCB[ii].MBRst^.oper.q.validity := validity_t.good;
		aCB[ii].MBCmd^.oper.trigger := (aCB[ii].MBCmd^.oper.ctlVal<>UINT_TO_DINT(CB_cmd));
		aCB[ii].MBRst^.oper.trigger := (aCB[ii].MBRst^.oper.ctlVal<>UINT_TO_DINT(CB_rst));
		aCB[ii].MBCmd^.oper.ctlVal := CB_cmd;
		aCB[ii].MBRst^.oper.ctlVal := CB_rst;

	END_IF
END_FOR

CB101_MODBUS_POU.EN := MBUS_ENA;
